package com.syntax.class01;

public class VariableValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte goal1=5;
		short goal2=500;
		int goal3=90360;
		long goal4=2500000;
		float thing1=4.99F;
		double thing2=8.01;
		char special1='b';
		char special2='*';
		boolean  win=true;
		boolean lose=false;
		goal1=8;
		goal2=888;
		thing1=88F;
		thing2=8.99;
		special1='u';
		win=false;
		
		System.out.println(goal1);
		System.out.println(goal2);
		System.out.println(goal3);
		System.out.println(goal4);
		System.out.println(thing1);
		System.out.println(thing2);
		System.out.println(special1);
		System.out.println(special2);
		System.out.println(win);
		System.out.println(lose);


				
				
				
				
				

	}

}
