package com.syntax.class02;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//+,*,-,/,%
		
		int a=900;
		int b=100;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);

		int c=10;
		int d=3;
		int sum=c+d;
		int sub=c-d;
		int remainder=c%d;
		int div=c/d;
		
		
		
	 System.out.println(sum);
	 System.out.println(sub);//10/3
	 
	 int e=10;
	 int f=5;
	 System.out.println("division ="+e/f);//2
	 System.out.println("remider="+e%f);//0
	 System.out.println("Remider is = "+ remainder);//1
	 System.out.println("Division of integers = "+ div);//3
	 double num1=10.0; 
	 double num2=3.0;
	 
	 System.out.println("Division of doubles = "+ num1/num2 );
     System.out.println(num1%num2);
     System.out.println("using float data type ---- ");
     float number1=10.0F;
     float number2=3.0F;
     System.out.println("Division of float "+number1/number2);
    		 
     
     
	}

}
